query.sql:

select busi_code as code from finance_task t where t.status=1
and t.busi_type=[busi_type]
{% if task_1 %}
   and  t.task_1 = [task_1]
{% endif%}
{% if task_2 %}
   and  t.task_2 = [task_2]
{% endif%}
{% if task_3 %}
   and  t.task_3 = [task_3]
{% endif%}
order by rand()
limit [limit]


update finance_task t set
t.update_time=now()
{% if task_1 %}
    ,t.task_1 = [task_1]
{% endif%}
{% if task_2 %}
    ,t.task_2 = [task_2]
{% endif%}
{% if task_3 %}
    ,t.task_3 = [task_3]
{% endif%}
where t.busi_type=[busi_type]
 {% if code_list %}
    and t.busi_code in ({% for c in code_list %}'{{ c.code }}'{% if not loop.last %},{% endif %}{% endfor %})
 {% endif%}
