query.sql:

SELECT * FROM plate
WHERE 1=1
{% if code %}
   and code = [code]
{% endif%}


select * from plate p where state=1
{% if type %}
    type = [type]
{% endif%}


select s.code,s.name,date_format(s.jj_time, '%Y%m%d') AS jj_time
 ,date_format(s.dj_time, '%Y%m%d') AS dj_time,b.buy_price,b.quantity,b.type,sf.mc fund_mc,sf.quantity as fund_quantity,
 sf.fund_count
 from stock s
{% if plate %}
join (SELECT
        stock_code
    FROM
        (
            SELECT
                stock_code
            FROM
                stock_plate p
            WHERE
                p.plate_code IN ({% for p in plate %}'{{ p }}'{% if not loop.last %},{% endif %}{% endfor %})
            AND p.state = 1
        ) s
    GROUP BY
        s.stock_code
    HAVING(count(stock_code)) >= {{ plate_count }}
    ORDER BY
        stock_code) tp on  s.code =tp.stock_code
 {% endif%}
 {% if  stock_buy  %} inner {% else %} left  {% endif%} join stock_buy b on  b.stock_code=s.code  and b.status=1
 left join stock_fund sf on sf.stock_code = s.code
where state=1
{% if code %}
    and s.code = [code]
{% endif%}
{% if name %}
    and s.name like concat("%",[name],"%")
{% endif%}
{% if type %}
    and s.type=[type]
{% endif%}
{% if dj_date %}
    and s.dj_time>=date([dj_date])
{% endif%}
{% if jj_date %}
    and s.jj_time>=date([jj_date])
{% endif%}
{% if is_jj %}
    and (s.jj_time<now() or s.jj_time is null)
{% endif%}
{% if stock_list %}
    and s.code in ({% for p in stock_list %}'{{ p }}'{% if not loop.last %},{% endif %}{% endfor %})
{% endif%}


select code from stock s where s.code!='1A0001' and s.code!='399001' and s.code!='399006'
and state=1

